module.exports.buildView = function(ele,shipper)
{
	shipper = shipper || $oc.shipper ;

	ele._children = {} ;
	ele._tpl = null ;

	$ele = jQuery(ele) ;

	ele.name = ele.controller = $ele.attr("controller") ;
	ele.params = $ele.attr("params") ;
	ele.sumsign = $ele.attr("sumsign") || null ;
	ele.onshowSrc = $ele.attr("onshow") ;

	// 加载onshow
	if( ele.onshowSrc )
	{
		shipper.require(ele.onshowSrc,function(err){
			if(err)
			{
				throw err ;
				return ;
			}

			var module = shipper.module(ele.onshowSrc) ;

			var type = typeof module ;
			if( type=="function" )
			{
				ele.onshow = function(){

					var $view = jQuery(ele) ;
					var $ = function(){
						return $view.find.apply($view,arguments) ;
					}
					$.__proto__ = jQuery ;

					module.call(ele,$) ;
				} ;
				ele.onshow() ;
			}
			else
			{
				throw new Error("onshow script of view must exports a function, but input is a "+type+", src: "+ele.onshowSrc) ;
			}
		}) ;
	}

	if(ele.sumsign)
	{
		$oc.views[ ele.sumsign ] = ele ;
	}

	return ele ;
}

