var View = require("ocPlatform/lib/mvc/view/View.js") ;

var FrontendViewTemplate = module.exports = View.extend({
	_readTemplateFile: function(callback)
	{
		$.ajax({
			url: "/shipdown:template/" + this.filePath
			, cache: true
			, dataType: "html"
			, success: function(data)
			{
				callback(null,data) ;
			}
			, error: function(req,txt)
			{
				callback(txt) ;
			}
		}) ;
	}
}) ;

module.exports.Caches = View.Caches.extend({
	ctor: function()
	{
		this._super() ;
		this._tempateclass = FrontendViewTemplate ;
	}
},{
	resolve: function(filename)
	{
		return filename ;
	}
}) ;
module.exports.Caches.singleton = new module.exports.Caches ;


module.exports.__SHIPPABLE = true ;
