
var IdManager = module.exports = function()
{
	this._ids = [] ;
	this._current = null ;
}

module.exports.connect_middleware = function(req,rsp,next)
{
	//console.log(req.session) ;

	if(!req.session.idmgr)
	{
		req.session.idmgr = new IdManager ;
	}

	//console.log(req.session.idmgr) ;

	next() ;
}