module.exports = function($)
{
	var view = this ;

	$(".miniuserpad-btn-signin").click(function(){

		var data = $(".miniuserpad-login").serializeArray() ;

		$oc.director.requestController('ocPlatform/lib/usr/SignIn:submit',data,function(err,nut){
			if(err)
			{
				console.log(err) ;
			}

			nut.msgqueue.popup() ;

			if( nut.model.signined )
			{
				view.reload() ;
			}
		})

	}) ;
}